import groovy.json.JsonSlurper
def Data = context.testCase.getTestStepByName("user login").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(Data)
def token = holder.accessToken
//Setting a varible in project level with the value
context.testCase.TestSuite.project.setPropertyValue("token","bearer "+token)

import groovy.json.JsonSlurper
def Data = context.testCase.getTestStepByName("get all users").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(Data)
def num = holder.count.toInteger()
log.info num
for(def i=0;i<num;i++)
{
	if(holder.users[i].email=="madhavilatha13@gmail.com")
	{
		  def userid=holder.users[i]._id
		  log.info userid
		  def converted_userid=userid.toString()
		  context.testCase.testSuite.project.setPropertyValue("userID",converted_userid)
	}
}

import groovy.json.JsonSlurper
def Data = context.testCase.getTestStepByName("Get all Products").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(Data)
def num = holder.count.toInteger()
log.info num
for(def i=0;i<num;i++)
{
	if(holder.products[i].name=="Lunch Bags")
	{
		  def productid=holder.products[i]._id
		  log.info productid
		  def converted_productid=productid.toString()
		  context.testCase.testSuite.project.setPropertyValue("ProductID",converted_productid)
	}
}
